mapper.select:
SELECT ID, BOARDNO, TITLE, CONTENT, REGDATE, SECRET
FROM QNA_BOARD
WHERE BOARDNO = #{boardno}

mapper.update:
UPDATE QNA_BOARD
SET TITLE = #{title}, CONTENT = #{content}
WHERE BOARDNO = #{boardno}

mapper.select:
SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM QNA_BOARD
					ORDER BY BOARDNO
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}
